  const womenColors = ['#8DA382', '#F5F0D9', '#E0D7C9', '#8B5E4B', '#D9A8B5'];
  const menColors = ['#D0D0D0', '#8DA382', '#E0D7C9', '#8B5E4B'];

  return (
    <section className={styles.dressCodeSection}>
      <div className={styles.dressCodeContent}>
